import { useCallback, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Title } from "../../../components/Title/Title";
import Firebase from "../../../firebase";
import { useInput } from "../../../hooks/useInput";
import "./SignUp.css";

export const SignUp = () => {
    const navigate = useNavigate();
    const [verifiedEmail, setVerifiedEmail] = useState(false);
    const [email, setEmail] = useInput("hksahenry@gmail.com");
    const [name, setName] = useInput("");

    const onInput = useCallback((set: React.Dispatch<string>, e: React.ChangeEvent<HTMLInputElement>) => {
        set(e.currentTarget.value);
    }, []);

    const onVerify = useCallback(async () => {
        setVerifiedEmail(await Firebase.instance.login({ email: email }));
    }, [email]);

    const onBackToLogin = useCallback(() => {
        navigate("/", { replace: true })
    }, []);

    return (
        <main id="signup">
            <Title />
            { !verifiedEmail &&
                <section id="verify_email">
                    <h3>Verify your email</h3>
                    <input type="text" value={ email } placeholder="Email" onChange={ (e) => onInput(setEmail, e) } />
                    <button onClick={ onVerify }>Verify</button>
                    <button onClick={ onBackToLogin }>Back to Login</button>
                </section>
            }
            { verifiedEmail &&
                <section id="signup_details">
                    <h3>Sign up</h3>
                    <div id="signup_row">
                        <div>Email:</div>
                        <div>{ email }</div>
                    </div>
                    <div id="signup_row">
                        <div>Name:</div>
                        <input type="text" value={ name } placeholder="Email" onChange={ (e) => onInput(setName, e) } />
                    </div>
                    <button onClick={ onBackToLogin }>Back to Login</button>
                </section>
            }
        </main>
    )
}
