import { collection, onSnapshot } from "firebase/firestore";
import { useEffect, useState } from "react";
import { Title } from "../../../components/Title/Title";
import Firebase, { PendingApplcation } from "../../../firebase";
import "./StudentList.css";

export const StudentList = () => {

    const [pendingList, setPendingList] = useState<Array<PendingApplcation>>([]);

    useEffect(() => {
        // Firebase.instance.getPendingAccounts().then(applications => setPendingList(applications));
        const unSubscribePendingAccountChange = onSnapshot(collection(Firebase.instance.firestore, "application"), collection => {
            let pendings: Array<PendingApplcation> = [];
            collection.forEach(application => pendings.push(Object.assign({ id: application.id }, application.data() as { name: string, email: string })));
            setPendingList(pendings)
        })
        return () => {
            unSubscribePendingAccountChange();
        }
    }, [])

    return (
        <main id="student_list">
            <Title />
            <section id="request">
                <h3>Pending accounts</h3>
                {   pendingList.map(application => (
                        <div key={ application.id } className="pending_account">
                            <div>{ application.name }</div>
                            <div>{ application.email }</div>
                            <button className="approve_icon" onClick={ () => {} } />
                            <button className="decline_icon" onClick={ () => {} } />
                        </div>
                    ))

                }
            </section>
        </main>
    )
}
