import './ExerciseRecord.css';

enum MONTH {
    JAN = 0,
    FEB,
    MAR,
    APR,
    MAY,
    JUN,
    JUL,
    AUG,
    SEP,
    OCT,
    NOV,
    DEC
}

const daysInYear = (year: number) => {
    return (new Date(year + 1, 0, 1).getTime() - new Date(year, 0, 1).getTime()) / 1000 / 60 / 60 / 24;
}

const weeksInMonth = (year: number, month: number) => {
    return Math.ceil((new Date(year, month - 1, 1).getDay() + new Date(year, month, 0).getDate()) / 7);
}

export const ExerciseRecord = () => {
    return (
        <section id="exercise_record" title=" Your work">
            <h3>Your work in 2022</h3>

            <section id="record_table">
                <div id="months">
                    <label>Jan</label>
                    <label>Feb</label>
                </div>
                <div id="days">
                    { Array(new Date(2020, 0, 1).getDay()).fill(null).map(_val => <div />) }
                    {
                        Array(daysInYear(2020)).fill(null).map((_val, i) =>
                            <div className="record_cell" key={ i }>{ i + 1 }</div>
                        )
                    }
                </div>
            </section>
        </section>
    )
}
