import "./DropdownMenu.css";

export const DropdownMenu = (props: { opened: boolean, default?: string, current?: string, choices: Array<string>, allowAdd?: boolean }) => {
    return (
        <div className="dropdown_menu">
            <button id="current"><div />{ props.current || props.default }<div className="arrow"/></button>
            {   props.opened &&
                <div id="choices">
                    { props.choices.map(choice => <button className="choice">{ choice }</button>) }
                    { props.allowAdd && <button className="choice">新增</button> }
                </div>
            }
        </div>
    )
}
