import './ExerciseRecord.css';

const daysInYear = (year: number) => {
    return (new Date(year + 1, 0, 1).getTime() - new Date(year, 0, 1).getTime()) / 1000 / 60 / 60 / 24;
}

export const ExerciseRecord = () => {
    return (
        <section id="exercise_record" title=" Your work">
            <h3>Your work in 2022</h3>
            <section id="record_table">
                { Array(6 - new Date(2021, 0, 1).getDay()).fill(null).map(_val => <div />) }
                {
                    Array(daysInYear(2021)).fill(null).map((_val, i) => <div className="record_cell" key={ i }>{ i + 1 }</div>)
                }

            </section>
        </section>
    )
}
