import { useCallback, useEffect, useState } from 'react';
import './ExerciseRecord.css';

enum MONTH {
    JAN = 0,
    FEB,
    MAR,
    APR,
    MAY,
    JUN,
    JUL,
    AUG,
    SEP,
    OCT,
    NOV,
    DEC
}

export const ExerciseRecord = () => {
    const [currentYear] = useState(2022);
    const [weeks, setWeeks] = useState(Array(12).fill(0));

    useEffect(() => {
        setWeeks(weeks.map((_val, month) => weeksInMonth(currentYear, month)));
    }, [])

    const daysInYear = useCallback((year: number) => {
        return (new Date(year + 1, 0, 1).getTime() - new Date(year, 0, 1).getTime()) / 1000 / 60 / 60 / 24;
    }, [])

    const weeksInMonth = useCallback((year: number, month: number) => {
        return Math.ceil((new Date(year, month, 1).getDay() + new Date(year, month, 0).getDate()) / 7);
    }, [])

    return (
        <section id="exercise_record" title=" Your work">
            <h3>Your work in { currentYear }</h3>

            <section id="record_table">
                <div id="months">
                    {
                        weeks.map((noOfWeeks, month) =>
                            {
                                let previousWeeks = weeks.slice(0, month).reduce((totalWeeks, currentWeeks, month) => totalWeeks + currentWeeks, 0);
                                console.log(previousWeeks);
                                return <label key={ MONTH[month] } style={ { gridColumn: `${previousWeeks + 1} / ${previousWeeks + noOfWeeks}` } }>{ MONTH[month].toLowerCase() }</label>
                            }
                        )
                    }
                </div>
                <div id="days">
                    { Array(new Date(currentYear, 0, 1).getDay()).fill(null).map((_val, i) => <div key={ i }/>) }
                    {
                        Array(daysInYear(currentYear)).fill(null).map((_val, i) => {
                            let dayInMonth = new Date(new Date(currentYear, 0).setDate(i + 1)).getDate();
                            return <div className="record_cell" key={ i }>{ dayInMonth }</div>
                        })
                    }
                </div>
            </section>
        </section>
    )
}
