import { useState } from "react";

export const useInput = (initialValue: string): [typeof initialValue, string | undefined, React.Dispatch<typeof initialValue>, React.Dispatch<React.SetStateAction<string | undefined>>] => {
    const [value, setValue] = useState(initialValue);
    const [error, setError] = useState<string | undefined>();

    return [value, error, setValue, setError];
}
