import { QueryDocumentSnapshot } from "firebase/firestore";

export type PendingApplcation = {
    id: string,
    name: string,
    email: string
    // apply_date:
}

class Option {
    title: string;
    isChoices: boolean;
    choices: Array<string> | undefined;

    constructor(title: string, isChoices: boolean, choices?: Array<string>) {
        if (isChoices) this.choices = choices;
        this.isChoices = isChoices;
        this.title = title;
    }
}

export const optionConverter = {
    toFirestore: () => {
        // let obj = {
        //     title: option.title,
        //     isChoices: option.isChoices,
        //     choices: option.choices
        // };
        // return data;
        return {}
    },
    fromFirestore: (snapshot: QueryDocumentSnapshot) => {
        const data = snapshot.data();
        return new Option(data.title, data.isChoices, data.choices);
    }
}
