import { QueryDocumentSnapshot } from "firebase/firestore";

export type PendingApplcation = {
    id: string,
    name: string,
    email: string
    // apply_date:
}

export type Option = {
    title: string,
    isChoices: boolean,
    choices?: Array<string>;
}

export const optionConverter = {
    toFirestore: () => {
        // let obj = {
        //     title: option.title,
        //     isChoices: option.isChoices,
        //     choices: option.choices
        // };
        // return data;
        return {}
    },
    fromFirestore: (snapshot: QueryDocumentSnapshot) => {
        const data = snapshot.data() as Option;
        return { id: snapshot.id, title: data.title, isChoices: data.isChoices, choices: data.choices };
    }
}
