import { useEffect, useState } from "react";
import { DropdownMenu } from "../../../components/DropdownMenu/DropdownMenu";
import { Title } from "../../../components/Title/Title";
import Firebase from "../../../firebase";
import "./Categories.css";

export const Categories = () => {

    const [categories, setCategories] = useState<Array<{ label: string, id: string }>>([]);
    const [categoryId, setCategoryId] = useState<string>();
    const [activeDropdown, setActiveDropdown] = useState<string>();
    const [options, setOptions] = useState<Array<{ id: string, choices?: Array<string>, isChoices: boolean, title: string }>>([]);

    useEffect(() => {
        let closeDropdown = () => {
            console.log("close dropdown");
            setActiveDropdown(undefined)
        };
        window.addEventListener("click", closeDropdown);

        return () => window.removeEventListener("click", closeDropdown);
    }, [])

    useEffect(() => {
        Firebase.instance.getCategories().then(categories =>
            setCategories(categories.map(category => {
                return { label: category.title, id: category.id }
            }))
        );
    }, [])

    useEffect(() => {
        if (categoryId) Firebase.instance.getOptions(categoryId).then(options => setOptions(options));
    }, [categoryId]);

    return (
        <main id="categories">
            <Title />
            <div id="categories-dropdown">
                <DropdownMenu onSelect={ setCategoryId } opened={ activeDropdown === "category"} onOpen={ () => setActiveDropdown("category") } onClose={ () => setActiveDropdown(undefined) } default={ "選擇分類" } current={ categories.find(cat => cat.id === categoryId)?.label } choices={ categories } allowAdd />
            </div>
        </main>
    )
}
